package acadgild;

public class VarOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int x = 5;
		int y =10;
		int a =  (x+y*2);
		int b = (x-y+2);
		int c = (x+y)*2;
		int d = y%x;
		System.out.println("x+y*2  :: "+ a);
		System.out.println("x+y*2  :: "+ b );
		System.out.println("x+y*2  :: "+ c);
		System.out.println("x+y*2  :: "+ d);
	}

}
